cd $PWD;pwd;ant compile && echo "Stopping mobile server" && ssh root@$1 'rcnovell-iprint-tomcat stop' &&  ssh root@$1 'rm -rf /var/opt/novell/iprint-tomcat/webapps/iprint*' && scp ./webapplication/target/iprint.war root@$1:/var/opt/novell/iprint-tomcat/webapps/iprint.war && echo Copied war file and starting mobile server && ssh root@$1 'rcnovell-iprint-tomcat start' && echo Mobile server started!
